import React, { useReducer } from 'react';
import uuid from 'uuid';
import ContactContext from './ContactContext';
import contactReducer from './contactReducer';
import {
  ADD_CONTACT,
  DELETE_CONTACT,
  SET_CURRENT,
  CLEAR_CURRENT,
  UPDATE_CONTACT,
  FILTER_CONTACTS,
  CLEAR_FILTER
} from '../types';

const ContactState = props => {
  const initialState = {
    contacts: [
      {
        id: 1,
        name: 'JJ',
        email: 'jj@gmail.com',
        phone: '111',
        type: 'personal'
      },
      {
        id: 2,
        name: 'SW',
        email: 's@gmail.com',
        phone: '222',
        type: 'personal'
      },
      {
        id: 3,
        name: 'HW',
        email: 'h@gmail.com',
        phone: '333',
        type: 'professional'
      }
    ]
  };

  // 等一下把参数颠倒，改名试试
  const [state, dispatch] = useReducer(contactReducer, initialState);

  // Add Contact
  const addContact = contact => {
    contact.id = uuid.v4();
    // dispatch an action. Here, action is an object
    dispatch({ type: ADD_CONTACT, payload: contact });
  };
  // Delete Contact

  // Set Current Contact

  // Update Contact

  // Filter Contacts

  // Clear Filter

  return (
    <ContactContext.Provider
      value={{
        contacts: state.contacts,
        addContact
      }}
    >
      {props.children}
    </ContactContext.Provider>
  );
};

export default ContactState;
